mapper.select:
select * from ${table.sqlName}

<#list table.columns as column>
    <if test="${column.columnNameLower}!=null <#if column.isStringColumn> and ${column.columnNameLower}!=''</#if>">
        and ${column.sqlName} = ${r"#{"}${column.columnNameLower}}

</#list>

mapper.update:
update ${table.sqlName}
<#list table.columns as column> <#if column.sqlName!='id' > <if test="${column.columnNameLower}!=null <#if column.isStringColumn > and ${column.columnNameLower}!=''</#if> "> ${column.sqlName} = ${r"#{"}${column.columnNameLower}},
</#if> </#list>
where id = ${r"#{"}id}
